import { useCart } from '../contexts/CartContext'
import { BsTrashFill } from 'react-icons/bs'

function Cart () {
  const { state: { items, total }, removeFromCart } = useCart()
  if (items.length < 1) {
    return (
      <h2>Votre panier est vide </h2>
    )
  }
  return (
    <>
      <h1>Cart</h1>
      <table border={1}>
        <thead>
          <th>Nom du produit</th>
          <th>Description du produit</th>
          <th>Prix unitaire</th>
          <th>Quantité</th>
          <th>Prix total</th>
          <th>Actions</th>
        </thead>
        <tbody>
          {
            items.map(cartItem => {
              return (
                <tr key={cartItem.item.attributes.id}>
                  <td> {cartItem.item.attributes.name}</td>
                  <td> {cartItem.item.attributes.description}</td>
                  <td> {cartItem.item.attributes.price || '10'}</td>
                  <td> {cartItem.item.qty}</td>
                  <td> {(cartItem.item.qty * cartItem.item.attributes.price).toFixed(2)} €</td>
                  <td>
                    <button onClick={() => removeFromCart(cartItem.item.id)}>

                      <BsTrashFill />
                    </button>
                  </td>

                </tr>
              )
            }
            )
          }
        </tbody>
        <tfoot>
          <tr>
            <td>Total</td>
            <td> {total} </td>
          </tr>
        </tfoot>
      </table>
    </>
  )
}

export default Cart
