import axios from 'axios'
import { toast } from 'react-toastify'

const api = axios.create({
  baseURL: process.env.REACT_APP_API_URL,
  headers: {
    'Content-Type': 'application/json',
    Accept: 'application/json'
  },
  timeout: 10000
})
api.interceptors.request.use(
  config => {
    console.log(config)
  },
  error => {
    return Promise.reject(error)
  }
)

const getRestaurants = async () => {
  try {
    const response = await api.get('/restaurants?populate=*')
    return response.data
  } catch (error) {
    console.error(error)
  }
}

const getRestaurantById = async (id) => {
  try {
    const response = await api.get(`/restaurants/${id}?populate=*`)
    return response.data
  } catch (error) {
    console.error(error)
  }
}

const getDishesByRestaurantId = async (id) => {
  try {
    const response = await api.get(`/dishes?filters[restaurant][id][$eq]=${id}&populate=*`)
    return response.data
  } catch (error) {
    console.error(error)
  }
}

const createRestaurant = async (formData) => {
  try {
    const authState = window.localStorage.getItem('AUTH')
    const auth = JSON.parse(authState)
    if (auth.user && auth.token) {
      const _data = {
        data: { // Spécifique à Strapi
          name: formData.name,
          description: formData.description,
          coordinates: {
            latitude: formData.latitude,
            longitude: formData.longitude
          }
        }
      }
    } else {
      toast.error("Vous n'êtes pas connecter")
    }
    const response = await api.post('/restaurants', _data)
    return response.data
  } catch (error) {
    console.error(error)
  }
}
const login = async (credential) => {
  const response = await api.post('auth/local', credential)
  return response.data
}

const register = async (credential) => {
  const response = await api.post('auth/local/register', credential)
  return response.data
}

export {
  getRestaurants,
  getRestaurantById,
  getDishesByRestaurantId,
  createRestaurant,
  login,
  register
}
