function Cart () {
  const { state: { items, total } } = useCart()
  return (
    <>
      <h1>cART</h1>
      <table>
        <thead>
          <th>Nom du produit</th>
          <th>Description du produit</th>
          <th>Prix unitaire</th>
          <th>Quantit√©</th>
          <th>Prix total</th>
          <th>Actions</th>
        </thead>
        <tbody>
          {
            items.map(cartItem => {
              return (
                <tr key={cartItem.item.id}>
                  <td> {cartItem.item.name}</td>
                </tr>
              )
            }
            )
          }
        </tbody>
      </table>
    </>
  )
}

